import { useLocation } from 'react-router-dom';
import logo from './images/omd_logo.png';
import logoO from './images/omdO.png';
import { FaBars } from 'react-icons/fa';
import Navbar from './navbar';

import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';

import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import Link from '@mui/material/Link';

import {
  usePopupState,
  bindTrigger,
  bindMenu,
} from 'material-ui-popup-state/hooks'


export default function Header() {
  const location = useLocation();
  const popupState = usePopupState({ variant: 'popover', popupId: 'demoMenu' })

  document.title = ((location.pathname).substring(1)).charAt(0).toUpperCase() + (location.pathname).substring(2) + ' - Organic Media Development';
  return (

    <AppBar position="relative" className="round-border">
        <Toolbar>
          <a  href="/contact"> <img src={logoO} className="App-logo-O" alt="logo" /></a>

          <Typography variant="body2"
              gutterBottom color="text.secondary" className="nav-header ease-in-ui" align="center" noWrap>
            <Link color="inherit" href="mailto:contact@organicmediadevelopment.com">
              contact@organicmediadevelopment.com</Link>
            </Typography>
          <div className="header-nav-bar">
      <a variant="contained" {...bindTrigger(popupState)}>
       <FaBars className="hand-link"/>
      </a>
      <Menu {...bindMenu(popupState)}>
        <MenuItem href="/contact" className={(location.pathname === '/contact' ? 'hidden' : '')} component="a" onClick={popupState.close}>Contact</MenuItem>
        <MenuItem href="/experience" className={(location.pathname === '/experience' ? 'hidden' : '')} component="a" onClick={popupState.close}>Experience</MenuItem>
        <MenuItem href="/portfolio" className={(location.pathname === '/portfolio' ? 'hidden' : '')} component="a" onClick={popupState.close}>Portfolio</MenuItem>
        <MenuItem href="/resume" className={(location.pathname === '/resume' ? 'hidden' : '')} component="a" onClick={popupState.close}>Resume</MenuItem>
      </Menu>
    </div>


        </Toolbar>
      </AppBar>

      );
}
