import * as React from 'react';
import {useState, useEffect, useCallback} from 'react';
import { render } from 'react-dom';
import ImageViewer from 'react-simple-image-viewer';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import Divider from '@mui/material/Divider';

/*
altourShowcase
limelightShowcase
qpmShowcase
showcaseelemental

chefryanShowcase
gatorShowcase
stardrumsShowcase

52 limited - oscillioscope and disney game translations

*/

export default function Album() {
  const [currentImage, setCurrentImage] = useState(0);
  const [isViewerOpen, setIsViewerOpen] = useState(false);

  const openImageViewer = useCallback((index) => {
    setCurrentImage(index);
    setIsViewerOpen(true);
  }, []);

  const closeImageViewer = () => {
    setCurrentImage(0);
    setIsViewerOpen(false);
  };

  const [data,setData]=useState([]);
  const [images,setImages]=useState([]);

  const getData=()=>{
    fetch('../portfolio.json'
    ,{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
    )
      .then(function(response){
        console.log(response)
        return response.json();
      })
      .then(function(myJson) {
        const images = [];
        myJson.map((item) => (
	images.push(item.image)
        ))
        setImages(images);
        setData(myJson)
      });
  }
  useEffect(()=>{
    getData()
  },[])


  return (

      <main>
        {/* Hero unit */}
        <Box
          sx={{
            bgcolor: 'background.paper',
            pt: 8,
            pb: 6,
          }}
        >
          <Container maxWidth="sm">
            <Typography
              component="h1"
              variant="h3"
              align="center"
              color="text.primary"
              gutterBottom
            >
              Portfolio
            </Typography>
            <Typography variant="h5" align="center" color="text.secondary" paragraph>
              Professional front end user interface software application developer with 20 years experience. 
            </Typography>

            <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
                </Stack>
          </Container>
        </Box>
        <Container sx={{ py: 8 }} maxWidth="md">
          {/* End hero unit */}
          <Grid container spacing={4}>
            {data.map((item, index) => (
              <Grid item key={item.id} xs={12} sm={6} md={4}>
                <Card
                  sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                >
                  <CardMedia
                    component="img"
                    sx={{
                      // 16:9
                      pt: '56.25%',
                    }}
                    image={item.image}
                    title={item.job}
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography  variant="h8" component="h4">
                      {item.company}
                    </Typography>
                    <Typography gutterBottom variant="body2">
                       {item.date}
                    </Typography><Divider />
                    <Typography  variant="h6" component="h2">
                       {item.title} 
                         </Typography>
                    <Typography gutterBottom variant="subtitle1">
                       {item.job}
                    </Typography><Divider />
                     <Typography variant="body4" >
                       {item.skills}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small" variant="contained" onClick={() => openImageViewer(index)}>View</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
                {isViewerOpen && (
        <ImageViewer
          src={images}
          currentIndex={currentImage}
          onClose={closeImageViewer}
          disableScroll={false}
          backgroundStyle={{
            backgroundColor: "rgba(0,0,0,0.8)"
          }}
          closeOnClickOutside={true}
        />
      )}
          </Grid>

        </Container>
      </main>

  );
}
